import classNames from 'classnames/bind';
import styles from './ManageLayout.module.scss';
import { Layout, Menu } from 'antd'; // Import Layout và Menu từ antd
import { Link } from 'react-router-dom'; // Giả sử bạn dùng react-router-dom cho điều hướng

const { Sider, Content } = Layout; // Destructure Sider và Content

const cx = classNames.bind(styles);

function ManageLayout({ children }) {
    // Bạn có thể quản lý trạng thái collapsed của Sider nếu muốn có nút thu gọn/mở rộng
    // const [collapsed, setCollapsed] = useState(false);

    return (
        <Layout className={cx('wrapper')}>
            {/* Sidebar */}
            <Sider
                // collapsible
                // collapsed={collapsed}
                // onCollapse={(value) => setCollapsed(value)}
                width={200} // Chiều rộng của sidebar
                className={cx('sidebar')}
            >
                <div className={cx('logo')}>
                    {/* Logo của bạn */}
                    <img
                        src="data:image/png;base64,"
                        alt="Logo"
                    />
                </div>
                <Menu
                    theme="light" // Hoặc "dark" nếu bạn muốn nền tối
                    mode="inline"
                    defaultSelectedKeys={['1']} // Đánh dấu mục "Posts" là được chọn mặc định
                    className={cx('menu')}
                >
                    <Menu.Item key="1" className={cx('menu-item')}>
                        <Link to="/manage/posts">Posts</Link> {/* Đường dẫn đến trang quản lý bài viết */}
                    </Menu.Item>
                    <Menu.Item key="2" className={cx('menu-item')}>
                        <Link to="/logout">Logout</Link> {/* Đường dẫn đến trang đăng xuất */}
                    </Menu.Item>
                    {/* Thêm các Menu.Item khác nếu có */}
                </Menu>
            </Sider>

            {/* Content chính */}
            <Layout className={cx('main-content-layout')}>
                {/* Bạn có thể thêm Header cho phần content ở đây nếu cần */}
                {/* <Header className={cx('content-header')}>
                    <span className={cx('page-title')}>Quản lý Bài viết</span>
                </Header> */}
                <Content className={cx('content')}>
                    {children} {/* Nội dung của các trang con sẽ được render ở đây */}
                </Content>
            </Layout>
        </Layout>
    );
}

export default ManageLayout;
