import React, { useState } from 'react';
import classNames from 'classnames/bind';
import styles from './Header.module.scss';
import ButtonComponent from '../../components/ButtonComponent/ButtonComponent';
import { MenuOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';

const cx = classNames.bind(styles);

function Header() {
    // State để quản lý trạng thái hiển thị của mobile-list
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // Hàm để toggle (ẩn/hiện) mobile-list
    const handleToggleMobileMenu = () => {
        setIsMobileMenuOpen(!isMobileMenuOpen);
    };

    const isLoggedIn = false;

    return (
        <div className={cx('wrapper')}>
            <div className={cx('logo')}>
                <img
                    src="data:image/png;base64,"
                    alt=""
                />
            </div>

            <div className={cx('header-actions')}>
                {/* Not Sign In */}
                <ButtonComponent
                    width="209px"
                    height="100%"
                    backgroundColor="#9c69e2"
                    border="none"
                    borderRadius="50px"
                    size="1.6rem"
                    weight="700"
                    textButton="Sign In"
                    link="login"
                />

                {/* Sign in */}
                {/* <ButtonComponent
                    width="209px"
                    height="100%"
                    backgroundColor="#9c69e2"
                    border="none"
                    borderRadius="50px"
                    size="1.6rem"
                    weight="700"
                    textButton="Profile"
                />
                <ButtonComponent
                    width="209px"
                    height="100%"
                    backgroundColor="#9c69e2"
                    border="none"
                    borderRadius="50px"
                    size="1.6rem"
                    weight="700"
                    textButton="Logout"
                /> */}
            </div>

            <div className={cx('mobile-menu-icon')} onClick={handleToggleMobileMenu}>
                <MenuOutlined style={{ fontSize: '24px', color: '#333' }} />
            </div>

            <div className={cx('mobile-list', { 'is-open': isMobileMenuOpen })}>
                {!isLoggedIn ? (
                    <ul>
                        <li>
                            <Link to="login" onClick={handleToggleMobileMenu}>
                                Sign In
                            </Link>{' '}
                            {/* Thêm link và đóng menu */}
                        </li>
                    </ul>
                ) : (
                    <ul>
                        <li>
                            <Link to="/profile" onClick={handleToggleMobileMenu}>
                                Profile
                            </Link>
                        </li>
                        <li>
                            <Link to="/logout" onClick={handleToggleMobileMenu}>
                                Logout
                            </Link>
                        </li>
                    </ul>
                )}
            </div>
        </div>
    );
}

export default Header;
